var React = require('react/addons');
var ClassNameBuilder = require('class-name-builder');

var Col = React.createClass({
  displayName: 'Column',
  _buildClassString: function() {
    var props = this.props;

    return ClassNameBuilder
      .create()
      .if(props.xsOffset, 'col-xs-' + props.lgOffset)
      .if(props.smOffset, 'col-sm-offset-' + props.lgOffset)
      .if(props.lgOffset, 'col-lg-offset-' + props.lgOffset)
      .if(props.mdOffset, 'col-md-offset-' + props.mdOffset)
      .if(props.xs, 'col-xs-' + props.xs)
      .if(props.sm, 'col-sm-' + props.sm)
      .if(props.md, 'col-md-' + props.md)
      .if(props.lg, 'col-lg-' + props.lg)
      .if(props.customClass, props.customClass)
      .toString();
  },
  render: function() {
    var classString = this._buildClassString();

    return(
      React.createElement("div", {className: classString}, 
        this.props.children
      )
    )
  }
});

module.exports = Col;
