var React = require('react/addons');
var ClassNameBuilder = require('class-name-builder');

var Container = React.createClass({
  displayName: 'Container',
  getDefaultProps: function () {
    return {
      fluid: false
    }
  },
  _buildClassString: function() {
    return ClassNameBuilder
      .create()
      .if(this.props.customClass, this.props.customClass)
      .if(this.props.fluid, 'container-fluid')
      .else('container')
      .toString();
  },
  render: function() {
    var classString = this._buildClassString();

    return(
      React.createElement("div", {className: classString}, 
        this.props.children
      )
    )
  }
});

module.exports = Container;
